SELECT Countries.continentExp AS continent, dataset.dateRep AS date, sum(cases) AS "number of cases", sum(deaths) AS "number of deaths" from dataset INNER JOIN Dates ON dataset.dateRep=Dates.dateRep INNER JOIN Countries on dataset.geoId = Countries.geoId group by Countries.continentExp, dataset.dateRep order by Countries.continentExp, Dates.year asc, Dates.month asc, Dates.day asc;